pragma solidity ^0.8.11; 

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

struct RoleData {
	mapping(address => bool) members;
	bytes32 adminRole;
}

abstract contract AccessControlEnumerable is Context {	
	event RoleAdminChanged(bytes32 indexed role, bytes32 indexed previousAdminRole, bytes32 indexed newAdminRole);
	event RoleGranted(bytes32 indexed role, address indexed account, address indexed sender);
    event RoleRevoked(bytes32 indexed role, address indexed account, address indexed sender);

    modifier onlyRole(bytes32 role) {
        _checkRole(role, _msgSender());
        _;
    }

    function renounceRole(bytes32 role, address account) public {
        require(account == _msgSender(), "AccessControl: can only renounce roles for self");
        _revokeRole(role, account);
    }

    function grantRole(bytes32 role, address account) public onlyRole(getRoleAdmin(role)) {
        _grantRole(role, account);
    }

    function revokeRole(bytes32 role, address account) public onlyRole(getRoleAdmin(role)) {
        _revokeRole(role, account);
    }

    function _checkRole(bytes32 role, address account) internal view {
        if (!hasRole(role, account)) {
            revert(
                string(
                    abi.encodePacked(
                        "AccessControl: account ",
                        Strings.toHexString(uint160(account), 20),
                        " is missing role ",
                        Strings.toHexString(uint256(role), 32)
                    )
                )
            );
        }
    }

	function hasRole(bytes32 role, address account) public view virtual returns (bool);

	function getRoleAdmin(bytes32 role) public view virtual returns (bytes32);

	function getRoleMember(bytes32 role, uint256 index) public view virtual returns (address);

	function getRoleMemberCount(bytes32 role) public view virtual returns (uint256);

	function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal virtual;

	function _grantRole(bytes32 role, address account) internal virtual;

	function _revokeRole(bytes32 role, address account) internal virtual;
}","keccak256":"0x2ec88ccf01386775c02afe3c7de92f73a2c8a1e846e2479d03683c067224e034","license":"GPL-3.0"},"contracts/tokens/RAIR Token Facets/AppStorage.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
